package com.javaex.practice;

public class Ex14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		
		[Ex14.java]
		아래 프로그램의 결과를 코드작성전에 먼저 예상해보고 코드를 작성해 확인해 보세요.
		
		public static void main(String[] args){
		
		   int a = 5;
		   
		   System.out.println(a);
		   System.out.println(++a);
		   System.out.println(a);
		   System.out.println(a++);
		   System.out.println(a);
		   System.out.println(--a);
		   System.out.println(a);
		   System.out.println(a--);
		   System.out.println(a);
		   	   
	----------------------------------------
		기댓값
		
		(1) 5
		(2) 6
		(3) 6
		(4) 6
		(5) 7
		(6) 6
		(7) 6
		(8) 6
		(9) 5
	
	---------------------------------------
	    결과값
	
	      5
	      6
          6
          6
          7
          6
          6
          6
          5
		
		*/
			 
		int a = 5;
		   System.out.println(a);
		   System.out.println(++a);
		   System.out.println(a);
		   System.out.println(a++);
		   System.out.println(a);
		   System.out.println(--a);
		   System.out.println(a);
		   System.out.println(a--);
		   System.out.println(a);
		
	}

}
