package com.javaex.practice;

public class Ex22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		
		[Ex22.java]
		아래의 출력결과를 예상하여 작성하세요.
		(코드를 작성해서 예상과 맞는지 확인해 보세요.)
		
		public static void main(String[] args){
		
		 System.out.println( 17 < 17);
		 System.out.println( 17 <= 17);
		 System.out.println( 5 >= 17);
		 System.out.println( 5 >= 5);
		 System.out.println( 5 == 17);
		 System.out.println( 5 != 17);
		 System.out.println( !(5==17) );
		 
		-------------------------------------------
		
		예상
		
		(1) 17 < 17  
		
		 -> 17은 17보다 작다.
		 -> false
		 
		(2) 17 <= 17
		
		 -> 17은 17보다 작거나 같다.
		 -> true
		 
		(3) 5 >= 17
		
		 -> 5는 17보다 크거나 같다.
		 -> false
		 
		(4) 5 >= 5
		
		 -> 5는 5보다 크거나 같다.
		 -> true
		 
		(5) 5 == 17
		
		 -> 5 는 17이다.
		 -> false
		 
		(6) 5 != 17
		
		 -> 5는 17과 다르다
		 -> true
		 
		(7) !(5==17)
		
		 -> 5는 17이다 의 결과값을 반대로 출력
		 -> true
		
		--------------------------------------
		
		결과값
		(1) false
		(2) true
		(3) false
		(4) true
		(5) false
		(6) true
		(7) true
		
		*/
		
		 System.out.println( 17 < 17);
		 System.out.println( 17 <= 17);
		 System.out.println( 5 >= 17);
		 System.out.println( 5 >= 5);
		 System.out.println( 5 == 17);
		 System.out.println( 5 != 17);
		 System.out.println( !(5==17) );

	}

}
